script:
{%- set vlan_count = devmeta.vlan_count|int -%}
{%- set site_subnet = devmeta.site_subnet -%}
{%- set vlan_cidr = devmeta.vlan_cidr|int %}
{%- set i = namespace(x=0) -%}
{%- for vlan in range(2,2+vlan_count) %}
  {%- set lan_block = site_subnet | ipaddr('subnet') | ipsubnet(vlan_cidr,i.x) -%}
config system interface
  edit "VLAN{{vlan}}"
    set vdom "root"
    set ip {{lan_block|ipaddr(1)|ipaddr('address')}} {{lan_block|ipaddr('netmask')}}
    set allowaccess ping
    set interface "fortilink"
    set vlanid {{vlan}}
    set description "FortiSOAR Script run for DeviceID {{record.id}}"
  next
end
  {% set i.x = i.x+1 -%}
{% endfor %}

{%- set table = [] -%}
{%- for i in interfaces -%}
  {%- set rec = {} -%}
  {%- if "ip" in i -%}
    {%- if i.ip[0] != "0.0.0.0" -%}
      {%- set subnet = ( i.ip[0] + "/" + i.ip[1] ) | ipsubnet -%}
      {%- set intip = subnet|ipaddr('cidr') -%}
    {%- else -%}
      {%- set intip = i.ip[0] -%}
    {%- endif -%}
  {%- else -%}
    {%- set intip = "no ip" -%}
  {%- endif -%}  
  {%- set _do = rec.update({
              "name":i.name,
              "description":i.description,
              "type":i.type,
              "ip": intip
  }) -%}    
  {%- set _do = table.append(rec) -%}
{%- endfor -%}

#### Interfaces

| Interface | Type | IP Address | Description |
| ---------- | ------ | ------------  | ------------- |
{% for r in table -%}
| {{r.name}} | {{r.type}} | {{r.ip}} | {{r.description}} |
{% endfor -%}
